package me.jerry.framework.dom;import java.lang.annotation.Documented;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * xml模型注解 * @author JerryGeng */@Documented@Retention(RetentionPolicy.RUNTIME)@Target(value = ElementType.TYPE)public @interface XmlBean {	/**	 * 对应的xml节点名称	 * @return	 */    String elementName () default "";}