package me.jerry.framework.dom;import java.lang.annotation.Documented;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * xml属性注解 * @author JerryGeng * */@Documented@Target(value=ElementType.FIELD)@Retention(RetentionPolicy.RUNTIME)public @interface XmlField {	/**	 * 属性名称	 * @return	 */    String attributeName() default "";    /**     * 如果该属性是一个新节点，表示节点对应的数据模型类型     * @return     */    Class listType () default Object.class;    /**     * 表示该属性是否是xml节点的content text。     * @return     */    boolean isContent() default false;}