package me.jerry.framework.utils;public class StringUtils {    public static String upTo_(String value){        String result = "";        for(int i = 0; i < value.length(); i ++) {            char ch = value.charAt(i);            if(ch <= 'Z' && ch >= 'A') {                if(i != 0) {                    value = new StringBuilder(value).insert(i, "_").toString();                }                i ++;            }        }        result = value.toLowerCase();        return result;    }    public static String _ToUp(String value, boolean firstUp) {        String[] ss = value.split("_");        String result = "";        boolean first = true;        for(String s : ss) {            if(first && !firstUp) {                result = s;                first = false;            } else {                result = result + s.substring(0, 1).toUpperCase() + s.substring(1);            }        }        return result;    }}